mapper.select:
SELECT docid as "docid" ,docttl as "docttl" ,gubun as "gubun" ,keyword as "keyword" ,authorid as "authorid" ,authorname as "authorname" ,authordutyname as "authordutyname" ,authorpositionid as "authorpositionid" ,authorgradeid as "authorgradeid" ,authordeptid as "authordeptid" ,authordeptname as "authordeptname" ,authordeptnamedesc as "authordeptnamedesc" ,lastauthorid as "lastauthorid" ,lastauthorname as "lastauthorname" ,state as "state" ,docnoseq as "docnoseq" ,open as "open" ,openbasis as "openbasis" ,openreason as "openreason" ,readrangetype as "readrangetype" ,readlimittype as "readlimittype" ,readlimitdate as "readlimitdate" ,delflag as "delflag" ,tempflag as "tempflag" ,indt as "indt" ,reportdt as "reportdt" ,enddt as "enddt" ,updt as "updt" ,upid as "upid" ,docfrom as "docfrom" ,moveflag as "moveflag" ,rcvsflag as "rcvsflag" ,transflag as "transflag" ,typeflag as "typeflag" ,protectflag as "protectflag" ,setupflag as "setupflag" ,oridocid as "oridocid" ,oriseq as "oriseq" ,batchseq as "batchseq" ,paperflag as "paperflag" ,maindocflag as "maindocflag" ,attachseq as "attachseq" ,enfgubun as "enfgubun" ,emergency as "emergency" ,drmgubun as "drmgubun" ,paperrecgubun as "paperrecgubun" ,paperrecspecial as "paperrecspecial" ,paperrectypes as "paperrectypes" ,migflag as "migflag" ,addsendstate as "addsendstate" ,addsendcnt as "addsendcnt" ,docgroup as "docgroup" ,faketitle as "faketitle" ,papersendflag as "papersendflag" ,filelendflag as "filelendflag" FROM sbms.sbms_user.bms_dct_rdoc WHERE tempflag = 'Y'
and authorid = #{authorid}
and docttl like CONCAT('%', #{docttl}, '%')
and indt >= #{startDt}
and indt <= #{endDt}
ORDER BY indt DESC

mapper.insert:
INSERT INTO sbms.sbms_user.bms_dct_rdoc(
docid,
docttl,
gubun,
keyword,
authorid , authorname , authordutyname , authorpositionid , authorgradeid , authordeptid , authordeptname , authordeptnamedesc , lastauthorid , lastauthorname , state , docnoseq , open , openbasis , openreason , readrangetype , readlimittype , readlimitdate , delflag , tempflag , inid , indt , reportdt , enddt , upid , updt , docfrom , moveflag , rcvsflag , transflag , typeflag , protectflag , setupflag , oridocid , oriseq , batchseq , paperflag , maindocflag , attachseq , enfgubun , emergency , drmgubun , paperrecgubun , paperrecspecial , paperrectypes , migflag , addsendstate , addsendcnt , docgroup , faketitle
) VALUES(
#{docid},
#{docttl},
#{gubun},
REGEXP_REPLACE(#{keyword}, '\s', '', 'g'),
#{authorid} , #{authorname} , #{authordutyname} , #{authorpositionid} , #{authorgradeid} , #{authordeptid} , #{authordeptname} , #{authordeptnamedesc} , #{lastauthorid} , #{lastauthorname} , #{state} , #{docnoseq} , #{open} , #{openbasis} , #{openreason} , #{readrangetype} , #{readlimittype} , #{readlimitdate} , #{delflag} , #{tempflag} , #{inid} , #{indt} , #{reportdt} , #{enddt} , #{upid} , #{updt} , #{docfrom} , #{moveflag} , #{rcvsflag} , #{transflag} , #{typeflag} , #{protectflag} , #{setupflag} , #{oridocid} , #{oriseq} , #{batchseq} , #{paperflag} , #{maindocflag} , #{attachseq} , #{enfgubun} , #{emergency} , #{drmgubun} , #{paperrecgubun} , #{paperrecspecial} , #{paperrectypes} , #{migflag} , #{addsendstate} , #{addsendcnt} , #{docgroup} , #{faketitle}
) ON CONFLICT (docid) DO UPDATE SET docttl = #{docttl}, gubun = #{gubun}, state = #{state}, open = #{open}, readrangetype = #{readrangetype}, emergency = #{emergency}, keyword = REGEXP_REPLACE(#{keyword}, '\s', '', 'g'), authorid = #{authorid}, authorname = #{authorname}, authordutyname = #{authordutyname}, authordeptid = #{authordeptid}, authordeptname = #{authordeptname}, tempflag = #{tempflag}, updt = #{updt}, reportdt = #{reportdt}

INSERT INTO sbms.sbms_user.bms_dct_summary(
docid,
docgubun,
summary
) VALUES(
#{docid},
#{docgubun},
#{summary}
) ON CONFLICT (docid, docgubun) DO UPDATE SET docgubun = #{docgubun}, summary = #{summary}

mapper.select:
SELECT A.docid as "docid" , A.docttl as "docttl" , A.reportdt as "reportdt" , A.authorid as "authorid" , A.authorname as "authorname" , A.authordutyname as "authordutyname" , A.authordeptid as "authordeptid" , A.authordeptname as "authordeptname" , A.updt as "updt" , A.state as "state" , A.tempflag as "tempflag" , A.emergency as "emergency" , A.paperflag as "paperflag" , A.paperrecgubun as "paperrecgubun" , E.acttype as acttype , C.docno as "docno" , C.copyno as "copyno" FROM sbms.sbms_user.bms_dct_rdoc A INNER JOIN sbms.sbms_user.bms_dct_attr C ON (C.docid = A.docid) LEFT OUTER JOIN sbms.sbms_user.bms_dct_mgmt_regi D ON (D.docid = A.docid) LEFT OUTER JOIN ( SELECT docid, acttype FROM sbms.sbms_user.bms_dct_path WHERE objid = #{authorid} ) E ON (E.docid = A.docid) WHERE A.tempflag = 'N' and A.authorid = #{authorid} and not exists( SELECT D.docid FROM sbms.sbms_user.bms_dct_doc_sendinfo D where A.docid = D.docid ) AND COALESCE(D.delflag, '') != 'Y' AND COALESCE(D.cnsrvflag , '') != 'Y'
AND (C.copyno LIKE '%원%' OR C.copyno IS NULL)
AND C.copyno NOT LIKE '%원%'
and E.acttype = #{acttype}
and C.docno like CONCAT('%', #{docno}, '%')
and A.docttl like CONCAT('%', #{docttl}, '%')
and A.state = #{state}
and A.reportdt >= #{startDt}
and A.reportdt <= #{endDt}
ORDER BY A.indt DESC

